def pure_python_dot_product(l1: List[Num], l2: List[Num]) -> float:
    dot = 0
    for x, y in zip(l1, l2):
        dot += x * y

    return dot

def numpy_dot_product(l1: List[Num], l2: List[Num]) -> float:
    dot = 0
    for x, y in zip(l1, l2):
        dot += x * y
    return dot

